            <Text
              style={[
                styles.severityText,
                { color: getSeverityColor(scan.severity) },
              ]}
            >
              {scan.severity}
            </Text>
          </View>
          <View
            style={[
              styles.statusBadge,
              { backgroundColor: getStatusColor(scan.status) + '20' },
